const int nb_lieux = 8;

const int NB_LIVREUR_MAX = 2;

const int unit_temps = 60;
const int temps_livraison_max = 1440;

typedef int[0, NB_LIVREUR_MAX-1] livreur_t;
typedef int[0, nb_lieux-1] nb_lieux_t;

bool lettres[nb_lieux_t];
bool chiens[nb_lieux_t];
bool grand_meres[nb_lieux_t];

int positions_livreurs[livreur_t];
int energies_livreurs[livreur_t];
const int ENERGIE_MAX = 2;

int nb_livreur = NB_LIVREUR_MAX;

bool grand_mere_is_active = true;
int time_to_wait_gm = 12 * unit_temps;
bool chien_is_active = true;
int time_to_wait_chien = 4 * unit_temps;


void ajoutEnergie(livreur_t idLivreur, int nrj){
    energies_livreurs[idLivreur] += nrj;
}

void perteEnergie(livreur_t idLivreur, int nrj){
    energies_livreurs[idLivreur] -= nrj;
}

