    //PLAN 1
    //grand_meres[3] = true;

    //PLAN 2
    grand_meres[7] = true;
}

void init_(){
    init_transitions(transitions);
    init_lettres(lettres);
    init_chiens(chiens);
    init_grand_meres(grand_meres);
    chienTimer = 0;
    grand_mereTimer = 0;
}

template.declaration:
bool tout_est_livre() {
    bool res = false;
    for(i : nb_lieux_t) {
        res = res or lettres[i];
    }
    return not res;
}

void grand_mere_swap_state(){
    grand_mere_is_active = not grand_mere_is_active;
}

int gm_guard(){
    return time_to_wait_gm;
}

int chien_guard(){
    return time_to_wait_chien;
}

void chien_swap_state(){
    chien_is_active = not chien_is_active;
    if(time_to_wait_chien == 2 * unit_temps){
        time_to_wait_chien = 6 * unit_temps;
    } else {
        time_to_wait_chien = 2 * unit_temps;
    }
}

clock x;

//temps à attendre
int time_to_wait;

int position = 0;

bool chien_actif(){
    return chiens[position] and chien_is_active;
}

//fonction de mise à jour de la position du livreur dans les variables globales
void maj_position(){
    positions_livreurs[id] = position;
}

void init_energie(){
    energies_livreurs[id] = ENERGIE_MAX;
}

//fonction de mouvement
void move_to(nb_lieux_t new_pos){
    time_to_wait = transitions[position][new_pos];
    position = new_pos;
    maj_position();
}

void rencontre(){
    if(chien_actif()){
        perteEnergie(id,1);
    }    
}

clock x;
clock y;
const int time_to_wait = 20;
int position = 0;

const int time_to_wait_gm = unit_temps * 2;


//fonction de mise à jour de la position du livreur avec les variables globales
void maj_position(){
    position = positions_livreurs[id];
}

bool grand_mere_active(){
    return grand_meres[position] and grand_mere_is_active;
}

bool chien_actif(){
    return chiens[position] and chien_is_active;
}
