const int lieux = 8;

const int NB_LIVREUR_MAX = 1;

const int unit_temps = 60;
const int temps_livraison_max = 1440;

typedef int[0, NB_LIVREUR_MAX-1] livreur_t;
typedef int[0, lieux-1] lieux_t;

//CHANNEL
chan livraison[livreur_t],epuise_livreur,verif_livraison;

//variables plan
int transitions[lieux_t][lieux_t];
